mapper.select:
select id,
name,
gender,
birthday,
nationality,
introduce,
avatar,
fans_count,
`status`,
create_by,
create_time,
update_by,
update_time,
is_deleted
from melody_artist


        and id = #{id}


        and name = #{name}


        and gender = #{gender}


        and birthday = #{birthday}


        and nationality = #{nationality}


        and introduce = #{introduce}


        and avatar = #{avatar}


        and fans_count = #{fansCount}


        and `status` = #{status}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}


limit #{offset},#{pageSize}

select count(1)
from melody_artist


        and id = #{id}


        and name = #{name}


        and gender = #{gender}


        and birthday = #{birthday}


        and nationality = #{nationality}


        and introduce = #{introduce}


        and avatar = #{avatar}


        and fans_count = #{fansCount}


        and `status` = #{status}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}
